select a.id id,a.resource_content resource_content,a.resource_summary resource_summary, a.resource_type_id resource_type_id,a.img_index img_index,a.img_index_size img_index_size,a.img_list img_list, a.img_list_size img_list_size,a.img_main img_main,a.img_main_size img_main_size,a.img_book img_book,a.img_book_size img_book_size, a.resource_status resource_status, date_format(a.create_date,'%Y-%m-%d %H:%i:%s') create_date,a.create_adminuser create_adminuser, date_format(a.update_date,'%Y-%m-%d %H:%i:%s') update_date,a.update_adminuser update_adminuser,b.type_name type_name, a.resource_keyword resource_keyword,a.resource_author resource_author,a.read_content read_content,a.read_img read_img, a.lian_content lian_content,a.lian_img lian_img,a.start_content start_content,a.start_img start_img from tbl_resource a left join tbl_resource_type b on a.resource_type_id=b.id INNER JOIN (SELECT resource_id FROM tbl_resource_info
property_id=#{property_id}
GROUP BY resource_id) c ON a.id=c.resource_id INNER JOIN (SELECT resource_id FROM tbl_resource_info
level_id=#{level_id}
GROUP BY resource_id) c2 ON a.id=c2.resource_id INNER JOIN (SELECT resource_id FROM tbl_resource_info
and language_level=#{language_level}
GROUP BY resource_id) c3 ON a.id=c3.resource_id
and a.id=#{id}
and a.resource_type_id=#{resource_type_id}
and 1=INSTR(a.resource_content,#{resource_content})
and a.resource_status=#{resource_status}
order by a.id desc

select a.id id,a.resource_content resource_content,a.resource_summary resource_summary, a.resource_type_id resource_type_id,a.img_index img_index,a.img_index_size img_index_size,a.img_list img_list, a.img_list_size img_list_size,a.img_main img_main,a.img_main_size img_main_size,a.img_book img_book,a.img_book_size img_book_size, a.resource_status resource_status, date_format(a.create_date,'%Y-%m-%d %H:%i:%s') create_date,a.create_adminuser create_adminuser, date_format(a.update_date,'%Y-%m-%d %H:%i:%s') update_date,a.update_adminuser update_adminuser,b.type_name type_name, a.resource_keyword resource_keyword,a.resource_author resource_author,a.read_content read_content,a.read_img read_img, a.lian_content lian_content,a.lian_img lian_img,a.start_content start_content,a.start_img start_img from tbl_resource a left join tbl_resource_type b on a.resource_type_id=b.id LEFT JOIN (SELECT resource_id FROM tbl_resource_info
property_id=#{property_id}
GROUP BY resource_id) c ON a.id=c.resource_id LEFT JOIN (SELECT resource_id FROM tbl_resource_info
level_id=#{level_id}
GROUP BY resource_id) c2 ON a.id=c2.resource_id LEFT JOIN (SELECT resource_id FROM tbl_resource_info
and language_level=#{language_level}
GROUP BY resource_id) c3 ON a.id=c3.resource_id
and a.id=#{id}
and a.resource_type_id=#{resource_type_id}
and 1=INSTR(a.resource_content,#{resource_content})
and a.resource_status=#{resource_status}
order by a.id desc

select id,resource_content,resource_summary,resource_type_id,img_index,img_index_size,
img_list,img_list_size,img_main,img_main_size,img_book,img_book_size,resource_status,
date_format(create_date,'%Y-%m-%d %H:%i:%s') create_date,create_adminuser,
date_format(update_date,'%Y-%m-%d %H:%i:%s') update_date,update_adminuser,
resource_keyword,resource_author,read_content,read_img,
lian_content,lian_img,start_content,start_img
from tbl_resource 


		 and  id=#{id}


		  and resource_content=#{resource_content}


		  and resource_type_id=#{resource_type_id}


		  and resource_status=#{resource_status}


 order by id

